create table if not exists products (
    id SERIAL PRIMARY KEY,
    code VARCHAR(255),
    name VARCHAR(255),
    description VARCHAR(255),
    image VARCHAR(255),
    category VARCHAR(255),
    price DECIMAL,
    quantity DECIMAL,
    internal_reference VARCHAR(255),
    shell_id BIGINT,
    inventory_status VARCHAR(50),
    rating DECIMAL,
    created_at BIGINT,
    updated_at BIGINT
);
